function validateEmail(email) {
  var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return re.test(email);
}

function validate() {
  var $result = $("#validate");
  var email = $("#email").val();
  $result.text("");

  if (validateEmail(email)) {
    $result.text("Отлично!");
    $result.css("color", "green");
  } else {
    $result.text("Введите корректный e-mail");
    $result.css("color", "red");
  }
  return false;
}
var inputBox = document.getElementById('email');
var len = 0;
inputBox.onkeyup = function(){
    validate();
}

/*Отправка для тестового аккаунта*/

var servResponse = document.querySelector('#servAnswer');
var servError = document.querySelector('#servError');
document.forms.tAccount.onsubmit = function(e){
    e.preventDefault();
    var answer = document.getElementById("email").value;
    answer = encodeURIComponent(answer);
    var xhr = new XMLHttpRequest();
    xhr.open('POST','email.php', true);
    xhr.setRequestHeader('Content-Type','application/x-www-form-urlencoded');
    xhr.onreadystatechange = function(){
       if(xhr.readyState === 4 && xhr.status === 200)
        {
			if(xhr.responseText.trim() == "error")
			{
				document.getElementById("servAnswer").style.display = "none";
				 setTimeout(function() { 
				   document.getElementById("servError").style.display = "block";	
			   }, 5000);
				servError.textContent = "Пользователь с таким email уже существует";
			}
             else if(xhr.responseText.trim() != "error")
			 {  
				 document.getElementById("servError").style.display = "none";
				 document.getElementById("servAnswer").style.display = "block";
                 servResponse.textContent = xhr.responseText;
		    }
        }
    }
    xhr.send('email='+answer);
};
